// ===============================================

window.RedStripe = (() => {
  function renderTable({ targetId, columns, data }) {
    const container = document.getElementById(targetId);
    if (!container)
      return console.error(`‚ùå RedStripe: No container with id ${targetId}`);

    let html = "<table><thead><tr>";
    columns.forEach((col) => {
      html += `<th>${col}</th>`;
    });
    html += "</tr></thead><tbody>";

    data.forEach((row) => {
      html += "<tr>";
      columns.forEach((col) => {
        html += `<td>${row[col] || ""}</td>`;
      });
      html += "</tr>";
    });

    html += "</tbody></table>";
    container.innerHTML = html;
  }

  return { renderTable };
})();
